import './game.css'

import { Title, Button } from '../../components';
import '../../Styles.css'


function Game() {
  const lines = 5

  function createLines(lines){
    const tab =[]
    
    for (let i = 1; i <= lines; i++) {
      var line = []
      for (let j = 1; j <= i; j++) {
        line.push(<button key={j} className='buttonGame'> ok </button>)
      }
      tab.push(<div key={i}>{line}</div>)
    }

    return <>{tab}</>
  }

  var player = "Player 1"
  function pass(){
    player === 'Player 1' ? player = 'Player 2' : player = "Player 1"
  }


  return (
    <div className="App">
      <div className='start'>
        <Title/>
        <p>{player}</p>

        {createLines(lines)}

        <Button onClick={pass}>Passar</Button>

      </div>
    </div>
    );
  }
  
  export default Game;
