import { useState } from 'react';

import './game.css'

import { Title, Button } from '../../components';
import '../../Styles.css'


function Game(props) {

    this.state={
        value:this.props.location.state,
    }


  function createLines(lines){
    const tab =[]
    
    for (let i = 1; i <= lines; i++) {
      var line = []
      for (let j = 1; j <= i; j++) {
        line.push(<button key={j} className='buttonGame'> ok </button>)
      }
      tab.push(<div key={i}>{line}</div>)
    }

    return <>{tab}</>
  }

  const [player, setPlayer] = useState("Player 1")
  const pass = () => {player === 'Player 1' ? setPlayer("Player 2") : setPlayer("Player 1")}


  return (
    <div className="App">
      <div className='start'>
        <Title/>
        <p>{player}</p>

        {createLines(props.lines)}
        {console.log(this.props.location.state)}

        <Button onClick={pass}>Passar</Button>

      </div>
    </div>
    );
  }
  
  export default Game;
