import './game.css'

import { Title, Button } from '../../components';
import '../../Styles.css'


function Game() {
  const lines = 7

  function createLines(lines){
    const tab =[]
    
    for (let i = 1; i <= lines; i++) {
      var line = []
      for (let j = 1; j <= i; j++) {
        line.push(<button className='buttonGame'> ok </button>)
      }
      tab.push(<div>{line}</div>)
    }

    return <div>{tab}</div>
  }


  return (
    <div className="App">
      <div className='start'>
        <Title/>
        <p>Player 1</p>

        {createLines(lines)}

        <Button>Passar</Button>

      </div>
    </div>
    );
  }
  
  export default Game;
